@bot.message_handler(commands=['start'])
def start_message(message):
    if message.text == '/start':
        bot.send_message(message.chat.id, '**–©–æ–± –ø–æ–±–∞—á–∏—Ç–∏ –≤—á–∏—Ç–µ–ª—ñ–≤ –Ω–∞–∂–º–∏ –Ω–∞ –∫–Ω–æ–ø–∫—É**', reply_markup=keyboard1, parse_mode="markdown")
        
